.graph-bar.time {
  --num: 0;
  width: calc((100% / var(--num)) - 4px) !important;
  margin: 0px 0px;
  position: relative;
}

.graph-bar.time p {
  display: none;


}

.graph-bar.time:hover p {
  position: absolute;
  text-align: center;
  display: block;
  top: -50px;
  width: 50px;
  margin: 0;
  padding: 3px;
  height: 14px;
  z-index: 100;
  color: white;
  background: rgba(0, 0, 0, 0.842);
  transform: translateY(-0px) translateX(-25px);
}

.graph-bar.time:hover p.hover-graph {
  transform: translateY(20px) translateX(-25px);
}

.graph {
  --max: 0;
  --red: 0;
  --green: 0;
  --blue: 0;
  display: flex;
  justify-content: space-evenly;
  align-items: flex-end;
  position: relative;
  width: 400px;
  height: 200px;
  background: rgba(226, 226, 226, 0.041);
  border-radius: 5px;
  padding-top: 10px;
}

.graph-labels {
  display: flex;
  position: relative;
  justify-content: space-between;
  align-items: flex-end;
  width: 400px;
}

.instructions {
  display: block;
  width: 100%;
  text-align: center;
  position: absolute;
  bottom: -23px;
  color: rgba(255, 255, 255, 0.082) !important;
}

.graph-labels p {
  margin: 5px 0;
  margin-top: 15px;
  font-size: .8em;

  color: rgba(250, 246, 246, 0.404);
  transform: translateY(-5px);
}

.yAxis {
  position: absolute;
  transform: rotate(-90deg);
  color: rgba(250, 246, 246, 0.404);
  font-size: .8em;
  left: -58px;
  top: 80px;

}

p {
  display: inline-block;

}

.graph-bar.toolow p {
  transform: translateY(-25px);
}

.graph-bar {
  --height: 0;
  display: block;
  width: calc(10% - 2px - 10px);
  margin: 0px 5px;
  height: calc((var(--height) / var(--max)) * 100% - 5px);
  padding-top: 5px;
  background: rgba(var(--red), var(--green), var(--blue), .9);
  border: 1px solid rgba(255, 255, 255, 0.151);
  border-top-left-radius: 4px;
  border-top-right-radius: 4px;
  animation: bar-graph-slide .5s ease-out .8s, peekaboo .8s;
}

.graph-bar p{
  color: rgba(255, 255, 255, 0.692);
  font-weight: bolder;
  margin: 0;
  font-size: .8em;
}

@keyframes bar-graph-slide {
  from {
    height: calc((var(--height) / var(--max)) * 0% - 5px);
    color: rgba(255, 255, 255, 0);
  }
}

a.row {
  text-decoration: none;
  display: flex;
  justify-content: left;
  align-items: center;
  padding: 20px 15px;
  background: rgba(255, 255, 255, 0.103);
  height: 60px;
  margin: 0px 32px;
  border-radius: 0px;
  text-decoration: none;
  font-family: 'Roboto', sans-serif;
}


a.row h1 {
  color: white;
  margin: 0 10px;
  font-weight: lighter;
  font-size: 28px;
  text-align: left;
  animation: none;
}


a.row h2 {
  color: rgb(250, 250, 250);
  margin: 0 10px 0px 0px;
  font-weight: bolder;
  font-size: 20px;
  width: 20px;
  animation: none;
}

.image {
  display: block;
  width: 60px;
  height: 60px;
  background-size: auto 100%;
  background-position: center center;
}

.table {
  width: 100%;
  margin: 0 auto;
  max-width: 1000px;
  margin-bottom: 100px;
  margin-top: 40px;
}

tr {
  --delay: 0;
  overflow: hidden;
  position: relative;
  animation: slide-up .5s ease calc(var(--delay) * .1s), peekaboo calc(var(--delay) * .1s) linear;
  border: 1px solid rgba(255, 255, 255, 0.116);
}

.comma {
  color: rgba(255, 255, 255, 0.514) !important;
  font-weight: lighter;
  text-transform: capitalize;
  font-size: 12px;
  margin: 0;
  margin-right: 7px;
}

.track-artists .track-artist {
  display: flex;
  flex-wrap: wrap;
  color: rgba(255, 255, 255, 0.514) !important;
  font-weight: lighter;
  text-transform: capitalize;
  font-size: 15px;
  margin: 0;
}

.track-artists {
  display: flex;
  margin-left: 13px;
}

.loadingspace {
  --delay: 0;
  margin-left: 13px;
  animation: slide-up .5s ease calc(var(--delay) * .1s), peekaboo calc(var(--delay) * .1s) linear, throb-row 2s ease-in-out calc(var(--delay) * .23s + var(--delay) * .1s + .5s) infinite;
}

@keyframes throb-row {
  0%{
    opacity: 1;
  }
  50%{
    opacity: .3;
  }
  100%{
    opacity: 1;
  }
}

.load#extremes-menu {
  margin-top: 40px;
  transition: all .3s ease;
}

.marginleft {
  margin-left: 15px;
}

#extremes-menu {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  margin: 5px auto;
  animation: slide-up .3s ease .3s, peekaboo .3s linear;
}

#extremes-menu h6 {
  color: rgba(252, 252, 252, 0.301);
  text-shadow: 1px 1px 10px rgba(255, 255, 255, 0.075);
  font-size: 40px;
  font-weight: lighter;
  margin: 0;
  font-family: 'Bitter', serif;
  font-family: 'Roboto', sans-serif;
}


#audio-features {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-bottom: 75px;
  padding: 32px;
  padding-top: 0px;
}
#menu {
  padding: 10px 32px;
}

#tabs {
  display: flex;
  justify-content: center;
  width: 100%;
}

.loading {
  margin-top: 30px;
}

.window {
  --delay: 0;
  animation: slide-up .5s ease calc(var(--delay) * .1s), peekaboo calc(var(--delay) * .1s);
  display: inline-block;
  width: 75%;
  margin: 20px 20px;
  padding: 20px;
  max-width: 400px;
  border-radius: 5px;
  margin-bottom: 20px;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.247);
}

h1 {
  color: white;
  text-align: left;
  animation: slide-up .3s ease 0s, peekaboo 0s linear;
}

h2 {
  color: rgba(255, 255, 255, 0.452);
  margin: 10px 20px;
  cursor: pointer;
  transition: all .3s ease;
  animation: slide-up .3s ease .1s, peekaboo .1s linear;
}

.row {
  display: flex;
  align-items: center;
}

.bar-row {
  justify-content: space-between;
}

h2:hover {
  color: rgba(255, 255, 255, 0.87);
}

h2.active {
  color: white;
}


.libraryanalysis {
  display: flex;
  width: 100vw;
  min-height: 100vh;
  position: absolute;
  top: 0;
  left: 0;
}

button {
  padding: 10px 50px;
  margin-top: 30px;
  background: rgba(255, 255, 255, 0.137);
  border-radius: 5px;
  color: rgb(255, 255, 255);
  border: 0px;
  font-weight: bolder;
  font-size: 1.2em;
  animation: slide-up .3s ease .3s, peekaboo .3s linear;
}

@keyframes slide-up {
  from {
    transform: translateY(100px);
    opacity: 0;
  }
}

h3 {
  margin: 5px;
  margin-top: 10px;
  color: white;
  animation: throb 2s ease 0s infinite;
}

@keyframes throb {
  0% {
    color: white;
    text-shadow: 0px 0px 5px rgba(255, 255, 255, 0.666);
  }
  50% {
    color: rgb(167, 167, 167);
    text-shadow: 0px 0px 0px rgba(255, 255, 255, 0.482);
  }
  100% {
     text-shadow: 0px 0px 5px rgba(255, 255, 255, 0.666);
  }
}
#progress-bar {
  display: block;
  width: 40%;
  height: 8px;
  overflow: hidden;
  border-radius: 5px;
  background: rgba(250, 250, 250, 0.173);
  margin: 0 auto;
  margin-top: 30px;
}

#progress-fill {
  --total: 0;
  --progress: 0;
  display: block;
  background: rgb(244, 244, 244);
  width: calc(var(--progress) / var(--total) * 100%);
  height: 100%;
}

img {
  display: block;
  width: 70px;
  height: 70px;
  margin-right: 20px;
}

h4 {
  color: white;
  display: flex;
  align-items: center;
  margin: 0;
  text-align: left;
}

.bar-title {
  width: 115px;
}

.stat-bar {
    display: block;
    border-radius: 5px;
    overflow: hidden;
    width: 200px;
    max-height: 10px;
    background-color: rgba(255, 255, 255, 0.247);
}

.stat-bar .fill {
    --percent: 0;
    --red: 255;
    --green: 255;
    --blue: 255;
    display: block;
    width: calc(var(--percent) * 100%);
    height: 10px;
    background: rgb(var(--red), var(--green), var(--blue));
    animation: slow-fill 1s ease;
}

@keyframes slow-fill {
  from {
    width: calc(var(--percent) * 0%);
  }
}

.window h3 {
  text-align: left;
  animation: none;
  font-size: 1.6em;
  margin: 0;
  margin-bottom: 20px;
}

.stat {
  margin-bottom: 15px;
  justify-content: space-between;
}

.value {
  width: 40px;
  text-align: right;
}

.nomargin {
  margin-bottom: 0 !important;
}

.favorite {
  font-size: 1.8em;
  text-transform: capitalize;
}

.favorite-div
{
  margin-top: 10px;
  margin-bottom: 10px;
  padding: 10px 10px;
  background: rgba(255, 255, 255, 0.062);
}

.favorite-div h5 {
  font-size: 1em;
  margin-top: 5px;
}



h5 {
  color: rgba(255, 255, 255, 0.514);
  margin: 0;
  text-align: left;
  font-size: .8em;
}

.light {
  color: rgba(255, 255, 255, 0.514) !important;
  font-size: 1.4em;
  margin-bottom: 2px;
}
.light h4 {
  margin-right: 5px;
